contracts/interface/IVerifier.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.0;

interface IVerifier {

    struct ClaimTransactionProof {
        string optionName;
        uint[2] pi_a;
        uint[2][2] pi_b;
        uint[2] pi_c;
        address eth_bridge_address;
        address eth_receiver;
        uint256 amount;
        uint160 cosmos_token_address;
        uint256 depositRoot;
    }

    struct DepositRootProof {
        string optionName;
        uint[2] pi_a;
        uint[2][2] pi_b;
        uint[2] pi_c;
        uint160 cosmosSender;
        uint160 cosmosBridge;
        uint256 depositRoot;
        uint160 dataHash;
    }

    struct ValidatorHashLeftProof {
        string optionName;
        uint[2] pi_a;
        uint[2][2] pi_b;
        uint[2] pi_c;
        uint256 totalVPsigned;
        uint256 totalVP;
        address[] validatorAddress;
        //address validatorHashAddress;
        //address dataHashAddress;
        //address blockHashAddress;
        uint256 signed;
    }

    struct ValidatorHashRightProof {
        string optionName;
        uint[2] pi_a;
        uint[2][2] pi_b;
        uint[2] pi_c;
        uint256 totalVPsigned;
        uint256 totalVP;
        address[] validatorAddress;
        //address validatorHashAddress;
        uint256 signed;
    }

    // Ex with msg = 111 optionName = "VERIFIER_VALIDATOR_SIGNATURE_111"
    // with msg = 110 optionName = "VERIFIER_VALIDATOR_SIGNATURE_110"
    struct SignatureValidatorProof {
        string optionName;
        uint[2] pi_a;
        uint[2][2] pi_b;
        uint[2] pi_c;
        //address validatorAddress
        //address blockHash
        //uint256 height
        //uint256 blockTime
        uint256 index;
    }

    /// @return r  bool true if proof is valid
    function verifyProof(
        uint[2] memory a,
        uint[2][2] memory b,
        uint[2] memory c,
        uint256[] memory input
    ) external view returns (bool r);
}

contracts/libs/Lib_AddressManager.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.0;

import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

/**
 * @title Lib_AddressManager
 * @notice Lib_AddressManager is a minimal contract that will store address of these contract on MainChain and SideChain
 */

contract Lib_AddressManager is
    OwnableUpgradeable
{
    address internal deployer;

    mapping(bytes32 => address) internal addresses;

    /*╔══════════════════════════════╗
      ║          CONSTRUCTOR         ║
      ╚══════════════════════════════╝*/

    function initialize() public initializer {
        require(
            deployer == address(0),
            "Lib_AddressManager already intialized"
        );

        deployer = msg.sender;
        // Initialize upgradable OZ contracts
        __Ownable_init_unchained();
    }

    /*  ╔══════════════════════════════╗
      ║        ADMIN FUNCTIONS       ║
      ╚══════════════════════════════╝ */

    /**
     * @dev set address contract by its name
     * @param _name name of contract
     * @param _address address of its
     */
    function setAddress(
        string memory _name,
        address _address
    ) external onlyOwner {
        bytes32 nameHash = _getNameHash(_name);
        addresses[nameHash] = _address;
    }

    /*╔══════════════════════════════╗
      ║            GETTERS           ║
      ╚══════════════════════════════╝*/

    function getAddress(string memory _name) external view returns (address) {
        return addresses[_getNameHash(_name)];
    }

    function _getNameHash(string memory _name) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked(_name));
    }
}

contracts/libs/Lib_AddressResolver.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {Lib_AddressManager} from "./Lib_AddressManager.sol";

/**
 * @title Lib_AddressResolver
 * @notice Lib_AddressResolver will resolve address contract from Lib_AddressManager
 */
abstract contract Lib_AddressResolver is Initializable {
    Lib_AddressManager public libAddressManager;

    function __Lib_AddressResolver_init(address _libAddressManager)
        internal
        onlyInitializing
    {
        libAddressManager = Lib_AddressManager(_libAddressManager);
    }

    /**
     * @dev get address of contract by its Name
     */
    function resolve(string memory _name) public view returns (address) {
        return libAddressManager.getAddress(_name);
    }
}

